public void generar(){
       code = (ap.getText().trim().substring(0, 2));
               code= code.concat(am.getText().trim().substring(0,1));
               code= code.concat(n1.getText().trim().substring(0,1));
               code= code.concat(an.getText().trim().substring(2, 4));
               code= code.concat(mn.getText().trim());
               code= code.concat(dn.getText().trim());
               JOptionPane.showMessageDialog(null, code.toUpperCase()); 
}
   
